$('#loginbtn').click(function() {
	if($("#username").val().length!=0 && $("#password").val().length!=0 && $("#verifycode").val().length!=0){
		var url = ctx+"/login/check";
		var data = {inputCode:$("#verifycode").val(),loginName:$("#username").val(),passWord:md5($("#password").val())};
		jQuery.post(url,data,function(msg){
			var obj = msg;// eval('('+msg+')');
			if(obj.isSuc){
				document.getElementById("loginForm").submit();
			}else{
				swal("失败", obj.errMsg, "error"); 
				changeImage();
				$("#verifycode").val("");
				return false;
			}
		});
	}
});
//回车提交事件
$("body").keydown(function() {
	if (event.keyCode == "13") {//keyCode=13是回车键
		$('#loginbtn').click();
	}
});

//验证码刷新
function changeImage(){
	document.getElementById("img").src = document.getElementById("img").src + '?';
}

function md5(input){
	return hex_md5(input);
}